//Styling for Custom Cursor
.cursor {
    position: absolute;
    height: 25px;
    width: 25px;
    border-radius: 50%;
    transform: translate(-50%, -50%);
    pointer-events: none;
    z-index:9999;
    background-color: none;
    border:2px solid $dark_grey;
    transition: height 0.3s ease-in-out, width 0.3s ease-in-out, background-color 0.3s ease-in-out;
    @include smartphone(){
        display: none;
    }
    @include tablet(){
        display: none;
    }
}

.hover{
    height: 20px !important;
    width: 20px !important;
    border:none;
    background-color: grey;
}

