//Basic Layout Containers and Elements
body{
    font-family: "Poppins";
    -ms-user-select: None;
    -moz-user-select: None;
    -webkit-user-select: None;
    user-select: None;
    cursor: none!important;
}

.base-section{
    height: 100vh;
}

.container{
    width: 90%;
    max-width: 1800px;
    margin-left: auto;
    margin-right: auto;
}

.flex-container{
    display: flex;
    flex-wrap:nowrap;
    align-items: center;
    justify-content: space-between;
    .flex-item{
        width: 50%;
        display: flex;
        flex-wrap: wrap;
        justify-content: center;
        align-items: center;
        @include smartphone(){
            width: 90%;
        }
    }
    .combi-item{
        display: flex;
        flex-direction: column;
        align-items: flex-start;
    }
}

sources.styles\\4-pages\\_loading.scss:
//Pre-Loading Screen on top of Home Page
.loading-screen{
    width: 100vw;
    z-index: 999;
    display: none;
    position: fixed;
    top:0;
    left:0;
    background-color: $white;
    background-image: $background-image-loading;
    .flex-container{
        justify-content:center;
        height: 100%;
        flex-direction: column;
        padding-top:10%;
        @include smartphone(){
            justify-content: flex-end;
            padding-top: 0%;
            padding-bottom: 25%;
        }
        img{
            width: 30%;
            @include smartphone(){
                width: 75%;
            }
            @include tablet(){
                width: 50%;
            }
        }
        .bt-loading{
            margin-top: 3%;
            opacity:0;
            transform: translateY(50%) rotateX(-80deg);
            transform-origin: center top;
            transform-style: preserve-3d;
            transition: opacity 0.7s cubic-bezier(.215,.61,.355,1),transform 1s cubic-bezier(.215,.61,.355,1);
            background: $transparent_grey;
            backdrop-filter: blur(10px);
            @include smartphone(){
                margin-top: 12%;
                margin-bottom: 25%;
            }
        }
        .bt-ready{
            opacity: 1 !important;
            transform: none !important;
        }
        p{
            color:$font_grey;
            font-size: 0.9rem;
            text-align: center;
            width: 40%;
            font-weight: 400;
            margin-top: 3%;
            opacity: 0;
            transition: opacity 1.2s cubic-bezier(.215,.61,.355,1);
            line-height: 1.2;
            @include smartphone(){
                width: 90%;
                margin-bottom: 3%;
            }
            @include tablet(){
                width: 70%;
                margin-top: 15%;
                margin-bottom: 2%;
            }
        }
        .pretext-loading{
            position: absolute;
            opacity: 1;
        }
        a{
            color:$font_grey;
            font-size:0.9rem;
            margin-top: 1%;
            opacity: 0;
            transition: opacity 1.2s cubic-bezier(.215,.61,.355,1);
        }
    }
}
.remove{
    display: none;
}

