                @include smartphone(){
                    font-size: 14vw;
                    position: relative;
                    left: 0;
                    top:0;
                    margin-bottom: 10%;
                }
            }
            .icon-container{
                display: flex;
                justify-content: center;
                @include smartphone(){
                    display: none;
                }
                .icon-box{
                    width: 13%;
                    img{
                        width: 100%;
                        border-radius: 50%;
                    }
                    p{
                        text-align: center;
                        opacity: 0;
                        transition: opacity 1s ease;
                        font-size: 0.8em;
                        width: 100%;
                        margin-top: 5%;
                    }
                }
                .icon-box:not(:last-child){
                    margin-right: 5%;
                    @include smartphone(){
                        margin-right: 0;
                    }
                }
                .icon-box:hover{
                    p{
                        opacity: 1;
                    }
                }
            }
            p{
                font-size: 1em;
                font-weight: 400;
                line-height: 1.4em;
                text-align: left;
                align-self: flex-end;
                width: 75%;
                margin-top: 5%;
                color:$font_grey;
                @include smartphone(){
                    text-align: left;
                    font-size: 5vw;
                    line-height: 1.4em;
                    width: 100%;
                    margin-top: 0;
                }
            }
        }
    }
}


//Background Gradient Classes
.color-red{
    background-image: $background-image-er;
    @include smartphone(){
        background-image: $background-image-er-phone; 
    }
}
.color-green{
    background-image: $background-image-abm;
    @include smartphone(){
        background-image: $background-image-abm-phone; 
    }
}
.color-orange{
    background-image: $background-image-oi;
    @include smartphone(){
        background-image: $background-image-oi-phone; 
    }
}

.color-multicol0{
    background-image: $background-image-multi0;
    @include smartphone(){
        background-image: $background-image-prod-gl-0-phone; 
    }
}


//Text Effect Class
.in-view .letters{
    opacity: 1 !important;
    transform: none !important;
}
.in-view .letters:nth-child(1){
    transition-delay: 0.2s!important;
}
.in-view .letters:nth-child(2){
    transition-delay: 0.4s!important;
}
.in-view .letters:nth-child(3){
    transition-delay: 0.6s!important;
}
.visible{
    opacity: 1 !important;
}
.appear{
    opacity: 1!important;
    transform: none !important;
}
.disappear{
    opacity: 0 !important;
}

