//Impress and Privacy Page

.impress-section{
    .flex-container{
        color:$font_grey;
        padding-top: 15%;
        padding-left: 4%;
        padding-right: 4%;
        flex-wrap: wrap;
        @include smartphone(){
            margin-top: 15%;
        }
            h1{
                color:$font_grey;
                font-size: 5.5em;
                font-weight: 800;
                line-height: 1em;
                @include smartphone(){
                    font-size: 14vw;
                }
                span{
                    width: 100%;
                    display:block;
                    opacity:0;
                    transform: translateY(50%) rotateX(-80deg);
                    transform-origin: center top;
                    transform-style: preserve-3d;
                    transition: opacity 0.8s cubic-bezier(.215,.61,.355,1),transform 1.2s cubic-bezier(.215,.61,.355,1);
                }
            }
        .landing-text{
            opacity: 0;
        }
        .flex-item{
            width: 100%;
            justify-content: flex-end;
            margin-top: 7%;
            @include smartphone(){
                margin-top: 15%;
                margin-bottom: 15%;
                font-size: 1.1rem;
            }
            @include tablet(){
                margin-top: 10%;
                margin-bottom: 15%;
                font-size: 1.1rem;
            }
            div{
                width: 60%;
                @include smartphone(){
                    width: 100%;
                }
                @include tablet(){
                    width: 70%;
                }
                p{
                    line-height: 1.4;
                    @include smartphone(){
                        font-size: 5vw;
                    }
                }
                h3{
                    font-weight: 600;
                    margin-bottom: 5px;
                    @include smartphone(){
                        font-size: 5vw;
                    }
                }
            }
            div:not(:last-child){
                margin-bottom: 3%;
                @include smartphone(){
                    margin-bottom: 5%;
                }
            }
        }
    }
}

