        }
        .flex-item{
            height: 35%;
            justify-content: flex-end;
            @include smartphone(){
                justify-content: center;
                height: 22%;
            }
            .landing-headline{
                color:$font_grey;
                font-size: 7vw;
                font-weight: 800;
                line-height: 1em;
                @include smartphone(){
                    font-size: 10vw;
                }
                .letters{
                display:block;
                opacity:0;
                transform: translateY(50%) rotateX(-80deg);
                transform-origin: center top;
                transform-style: preserve-3d;
                transition: opacity 0.8s cubic-bezier(.215,.61,.355,1),transform 1.2s cubic-bezier(.215,.61,.355,1);
                }
            }
            .landing-text{
                color:$font_grey;
                font-size: 2.2vw;
                font-weight: 400;
                line-height: 1.3;
                opacity: 0;
                @include smartphone(){
                    font-size: 6vw;
                    line-height: 1.3;
                    text-align: left;
                }
            }
        }
    }
}

