.link-underline::before{
    content:'';
    transform-origin: 100% 50%;
    transform: scale3d(0, 1, 1);
    transition: transform ease-out 0.4s;
    position: absolute;
    width: 100%;
    height: 1.5px;
    background: black;
    border-radius: 100px;
    top: 100%;
    left: 0;
    pointer-events: none;
}
  
