//Link Styles with Hover-Animation
a{
    text-decoration: none;
    color: $font_grey;
}

a:hover{
    cursor: none;
}

.link-underline{
    cursor: none;
    position: relative;
    white-space: nowrap;
    text-decoration:none;
}

.link-underline::before{
    content:'';
    transform-origin: 100% 50%;
    transform: scale3d(0, 1, 1);
    transition: transform ease-out 0.4s;
    position: absolute;
    width: 100%;
    height: 1.5px;
    background: $font_grey;;
    border-radius: 100px;
    top: 100%;
    left: 0;
    pointer-events: none;
}
  
.link-underline:hover::before{
    transform-origin: 0% 50%;
    transform: scale3d(1, 1, 1);
}

sources.styles\\3-components\\_cursor.scss:
//Styling for Custom Cursor
.cursor {
    position: absolute;
    height: 25px;
    width: 25px;
    border-radius: 50%;
    transform: translate(-50%, -50%);
    pointer-events: none;
    z-index:9999;
    background-color: none;
    border:2px solid $dark_grey;
    transition: height 0.3s ease-in-out, width 0.3s ease-in-out, background-color 0.3s ease-in-out;
    @include smartphone(){
        display: none;
    }
    @include tablet(){
        display: none;
    }
}

.hover{
    height: 20px !important;
    width: 20px !important;
    border:none;
    background-color: grey;
}

sources.styles\\1-utilities\\_mixins.scss:
//SCSS-Mixins for Responsive Design

//Breakpoint for Smartphone
@mixin smartphone {
    @media only screen and(max-width: $screen-small){
        @content;
    }
}

//Breakpoint for Tablet
@mixin tablet {
    @media only screen and(max-width: $screen-medium) and
    (min-width: $screen-small){
        @content;
    }
}

sources.styles\\3-components\\_header.scss:
//Fully Responsive Page Header which is show on every page
header{
    z-index: 99;
    position: fixed;
    width: 100vw;
    top: 4vh;
    margin: auto;
    .flex-container{
        width: 90%;
        margin: auto;
        padding: 1.5% 4%;
        border-radius: 50px;
        background-color: $transparent_grey;
        backdrop-filter: blur(10px);
        @include smartphone(){
            background: none;
            backdrop-filter: none;
        }
        .logo{
            width: 12%;
            @include smartphone(){
                width: 130px
            }
            img{
                width: 13%;
                position: absolute;
                top:20%;
                @include smartphone(){
                    width: 130px;
                    top:15%
                }
            }
        }
        nav{
            @include smartphone(){
                position: fixed;
                top:0;
                left:0;
            }
            .menu{
                list-style: none;
                display: flex;
                @include smartphone(){
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;
                    height: 50px;
                    width: 50px;
                    border-radius: 100px;
                    font-weight: 500;
                    font-size: 1.8rem;
                    background-color: $transparent_grey;
                    backdrop-filter: blur(10px);
                    position: fixed;
                    right: 8%;
                    top: 3%;
                    transition: all 0.6s ease-in-out;
                }
                li:not(:last-child){
                    margin-right: 2.5em;
                    @include smartphone(){
                        margin-bottom: 2.5em;
                        margin-right: 0;
                    }
                    @include tablet(){
                        margin-right: 1.8em;
                    }
                }
                li{ 
                    @include smartphone(){
                        visibility: hidden;
                    }
                    a{
                        font-size: 1em;
                        color:$font_grey;
                        @include smartphone(){
                            width: 100%;
                            display:block;
                            opacity:0;
                            transform: translateY(50%) rotateX(-80deg);
                            transform-origin: center top;
                            transform-style: preserve-3d;
                            transition: opacity 0.8s cubic-bezier(.215,.61,.355,1),transform 1.2s cubic-bezier(.215,.61,.355,1);
                        }
                        @include tablet(){
                            font-size: 0.8em;
                        }
                    }
                }
            }
        }
        .hamburger{
            display: none;
            @include smartphone(){
                display: block;
                position: fixed;
                top: 3%;
                right: 8%;
                width: 50px;
                height: 50px;
            }
            span{
                display: block;
                position: absolute;
                width: 30px;
                height: 3px;
                background-color: $dark_grey;
                border-radius: 50px;
                transition: .25s ease-in-out;
                left: 10px;
            }
            span:nth-child(1){
                top: 15px;
            }
            span:nth-child(2){
                top: 23px;
            }
            span:nth-child(3){
                top: 23px;
            }
            span:nth-child(4){
                top: 31px;
            }
        }
        .hamburger.open span:nth-child(1){
            top: 20px;
            width: 0%;
            //left: 50%;
        }
        .hamburger.open span:nth-child(2){
            transform: rotate(45deg);
        }
        .hamburger.open span:nth-child(3){
            transform: rotate(-45deg);
        }
        .hamburger.open span:nth-child(4){
            top: 20px;
            width: 0%;
            //left: 50%;
        }
    }
}

//Classes to add on mobile when opening the menu
.menu-open{
    @include smartphone(){
        width: 100vw !important;
        height: 100vh !important;
        border-radius: 0px !important;
        top: 0 !important;
        right: 0 !important;
    }
}

.show-list-items{
    @include smartphone(){
        visibility:visible !important;
    }
}

.show-links{
    opacity: 1 !important;
    transform: none !important;
}

#link1{
    transition-delay: 0.9s!important;
}

#link2{
    transition-delay: 1.1s!important;
}

#link3{
    transition-delay: 1.3s!important;
}

#link4{
    transition-delay: 1.5s!important;
}

sources.styles\\3-components\\_footer.scss:
//Fully Responsive Page Footer which is show on every page

footer{
    width: 100vw;
    .flex-container{
        width: 90%;
        margin:auto;
        padding: 5% 4%;
        @include smartphone(){
            flex-direction: column-reverse;
            padding: 8% 4%;
        }
        @include tablet(){
            flex-direction: row;
            padding: 0% 4% 10% 4%;
            flex-wrap: wrap-reverse;
        }
        .logo{
            width: 12%;
            @include smartphone(){
                width: 100%;
                display:flex;
                justify-content: space-between;
                align-items: center;
            }
            @include tablet(){
                width: 100%;
                display:flex;
                justify-content: space-between;
                align-items: center;
                margin-top: 10%;
            }
            img{
                width: 100%;
                @include smartphone(){
                    width: 40%;
                }
                @include tablet(){
                    width: 30%;
                }
            }
            span{
                font-size: 0.8rem;
                @include smartphone(){
                    font-size: 1.3rem;
                }
                @include tablet(){
                    font-size: 1.5rem;
                }
            }
        }
        .links{
            color:$font_grey;
            flex-grow: 0.2;
            display: flex;
            justify-content: space-between;
            @include smartphone(){
                width: 100%;
                flex-grow: 0;
                font-size: 1.2rem;
                margin-bottom: 15%;
                ul:last-child{
                    display: none;
                }
            }
            @include tablet(){
                flex-grow: 0.6;
                font-size: 0.9em;
            }
            ul{
                li:not(:last-child){
                    margin-bottom: 10px;
                }
            }
        }
        .contact{
            @include smartphone(){
                width: 100%;
                margin-bottom: 15%;
            }
            #text{
                color:$font_grey;
                @include smartphone(){
                    font-size: 7vw;
                }
                @include tablet(){
                    font-size: 2.5vw;
                }
            }
            #mail{
                margin-top: 10px;
                a{
                    color:$font_grey;
                    font-weight: 700;
                    font-size: 1.2rem;
                    @include smartphone(){
                        font-size: 8vw;
                    }
                    @include tablet(){
                        font-size: 3vw;
                    }
                }
            }
        }
    }
}

