//Home Page

//Landing Section
.landing-section-home{
    .bgvideo{
        object-fit: cover;
        width: 100vw;
        height: 100vh;
        position: fixed;
        top: 0;
        left: 0;
        opacity: 0.8;
        transition: opacity 0.1s ease;
        @include smartphone(){
            position: absolute;
        }
    }
    .flex-container{
        justify-content: center;
        height: 100%;
        @include smartphone(){}
        .flex-item{
            height: 100%;
            h1{
                font-size: 9vw;
                font-weight: 800;
                line-height: 1em;
                color:rgba(255, 255, 255, 0.788);
                mix-blend-mode: hard-light;
                @include smartphone(){
                    font-size: 14vw;
                }
                span{
                    width: 100%;
                    display:block;
                    opacity:0;
                    transform: translateY(50%) rotateX(-80deg);
                    transform-origin: center top;
                    transform-style: preserve-3d;
                    transition: opacity 0.8s cubic-bezier(.215,.61,.355,1),transform 1.2s cubic-bezier(.215,.61,.355,1);
                }
            }
        }
    }
}

//Product Section "Erdbeer-Rhabarber"
.product-section-er{
    .flex-container{
        height: 100%;
        @include smartphone(){
            padding-top: 20%;
            flex-direction: column-reverse;
            justify-content: flex-end;
        }
        .canvas-box{
            opacity: 0;
            transition: opacity 0.3s ease;
            @include smartphone(){
                position: relative;
                top:-20%;
                left:-50%;
            }
        }
        .combi-item{
            @include smartphone(){
                align-items: center;
            }
        }
        .flex-item{
            canvas{
                position: relative;
                top: 0;
                left: 0;
                display: block;
                @include smartphone(){
                    position: absolute;
                    display: none;
                    transform: rotate(8deg) !important;
                    left:35%
                }
            }
            .text-box{
                h2{
                font-size: 4.5em;
                font-weight: 800;
                line-height: 1em;
                color:$font_grey;
                @include smartphone(){
                    font-size: 14vw;
                }
                    span{
                        width: 100%;
                        display:block;
                        opacity:0;
                        transform: translateY(50%) rotateX(-80deg);
                        transform-origin: center top;
                        transform-style: preserve-3d;
                        transition: opacity 0.8s cubic-bezier(.215,.61,.355,1),transform 1.2s cubic-bezier(.215,.61,.355,1);
                    }
                }
            }
            .bt-er{
                background: $transparent_grey;
