//Info Section
.info-section-home{
    @include smartphone(){
        height: 75vh;
    }
    .flex-container{
        justify-content: center;
        height: 100%;
        @include smartphone(){
            justify-content: center;
            width: 85%;
        }
        .flex-item{
            width: 70%;
            flex-direction: column;
            @include smartphone(){
                width: 90%;
                margin-left: 0;
            }
            h4{
                width: 100%;
