a{
    text-decoration: none;
    color: black;
}

a:hover{
    cursor: none;
}

.link-underline{
    cursor: none;
    position: relative;
    white-space: nowrap;
    text-decoration:none;
}

.link-underline::before{
    content:'';
    transform-origin: 100% 50%;
    transform: scale3d(0, 1, 1);
    transition: transform ease-out 0.4s;
    position: absolute;
    width: 100%;
    height: 1.5px;
    background: black;
    border-radius: 100px;
    top: 100%;
    left: 0;
    pointer-events: none;
}
  
.link-underline:hover::before{
    transform-origin: 0% 50%;
    transform: scale3d(1, 1, 1);
}

