//Pre-Loading Screen on top of Home Page
.loading-screen{
    width: 100vw;
    z-index: 999;
    position: fixed;
    top:0;
    left:0;
    background-color: $transparent_white;
    .flex-container{
        justify-content:center;
        height: 100%;
        flex-direction: column;
        padding-top:10%;
        img{
            width: 30%;
        }
        .bt-loading{
            margin-top: 3%;
            opacity:0;
            transform: translateY(50%) rotateX(-80deg);
            transform-origin: center top;
            transform-style: preserve-3d;
            transition: opacity 0.7s cubic-bezier(.215,.61,.355,1),transform 1s cubic-bezier(.215,.61,.355,1);
        }
        .bt-ready{
            opacity: 1 !important;
            transform: none !important;
        }
        p{
            font-size: 0.9rem;
            text-align: center;
            width: 40%;
            font-weight: 400;
            margin-top: 3%;
            opacity: 0;
            transition: opacity 0.7s cubic-bezier(.215,.61,.355,1);
            transition-delay: 0.3s;
        }
        a{
            font-size:0.9rem;
            margin-top: 1%;
            opacity: 0;
            transition: opacity 0.7s cubic-bezier(.215,.61,.355,1);
            transition-delay: 0.3s;
        }
    }
}
.remove{
    display: none;
}

